mapper.select:
select id, purchase_id, created_at, is_deleted, is_completed
from nthing.chat_room
where id = #{int}

select id, purchase_id, created_at, is_deleted, is_completed
from nthing.chat_room
where purchase_id = #{purchaseId}

mapper.update:
update nthing.chat_room_user
set left_at = now()
where user_id = #{userId} and chat_room_id = #{chatRoomId};
