mapper.select:
SELECT p.id, p.title, p.latitude, p.longitude, p.date, p.denominator, p.numerator, p.price, p.place, p.status, ( SELECT COUNT(*) FROM nthing.like l WHERE true AND l.user_id = #{user_id} AND l.purchase_id = #{id} ) AS is_liked, ( SELECT save_name FROM nthing.purchase_file WHERE true AND purchase_id = p.id AND delete_yn = false ORDER BY id LIMIT 1 ) AS image, ST_Distance_Sphere(@location, POINT(p.longitude, p.latitude)) AS distance FROM nthing.purchase AS p WHERE true AND p.delete_yn = 0 AND ST_Distance_Sphere(@location, POINT(p.longitude, p.latitude)) <= #{radius}
AND (p.title LIKE CONCAT('%', #{search_keyword}, '%') OR p.description LIKE CONCAT('%', #{search_keyword}, '%'))
AND p.status = false
ORDER BY ${sort}
ORDER BY -p.created_at

SELECT COUNT(*) > 0 AS isExist
FROM nthing.purchase
WHERE id = #{id}
    AND manager_id = #{user_id}
    AND delete_yn = 0
